import pytest
import time
import json
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.support import expected_conditions as EC
from selenium.webdriver.support.wait import WebDriverWait
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.desired_capabilities import DesiredCapabilities

class TestRep():
  def setup_method(self, method):
    self.driver = webdriver.Chrome()
    self.vars = {}
    self.wait = WebDriverWait(self.driver, 10)
  
  def teardown_method(self, method):
    self.driver.quit()
  
  def test_rep(self):
    try:
      # Navigate to homepage and login
      self.driver.get("http://localhost/new/")
      self.driver.set_window_size(1327, 736)
      
      # Click login link and wait for login form
      login_link = self.wait.until(EC.presence_of_element_located((By.LINK_TEXT, "Login")))
      login_link.click()
      
      # Wait for and fill login form
      email_field = self.wait.until(EC.presence_of_element_located((By.ID, "email")))
      email_field.click()
      email_field.send_keys("milan@gmail.com")
      
      password_field = self.driver.find_element(By.ID, "password")
      password_field.send_keys("Milan@07")
      password_field.send_keys(Keys.ENTER)
      
      # Wait for dashboard to load and find Reports dropdown
      reports_dropdown = self.wait.until(
        EC.presence_of_element_located((By.XPATH, "//a[contains(., 'Reports') or contains(., 'reports')]"))
      )
      reports_dropdown.click()
      
      # Wait for dropdown menu to appear and click Sales Reports
      sales_reports = self.wait.until(
        EC.presence_of_element_located((By.LINK_TEXT, "Sales Reports"))
      )
      sales_reports.click()
      
    except Exception as e:
      # Take a screenshot for debugging
      self.driver.save_screenshot("test_rep_error.png")
      raise Exception(f"Test failed: {str(e)}")
  
